import React, { useState } from 'react';
import Chatapp from './Image/Chatapp.png';
 //import SendIcon from '@material-ui/icons/Send';
// import EmailIcon from '@material-ui/icons/Email';
// import Button from '@material-ui/core/Button';  variant="outlined" color="secondary"
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
const Chat =()=>{
    const[text,setText] = useState("");
    const[newtext,updatetext] = useState([]);
    var time1 = new Date().toLocaleTimeString();

    const onEvent=(main)=>{
     setText(main.target.value);
      console.log(setText);
    }
    const onSubmit=(event)=>{
      updatetext((even)=>{
         return [...even,text];
      })
     setText('');
      event.preventDefault();
    }
     newtext.map((val)=>{
       return val;
     }) 
    return(<>
        <div className="chat_box">
         <img src={Chatapp} alt ="background" width="100%" height="570px"/>
         <div className="Chat_head">
         <h2>Imran</h2>
         </div>
         <div className="chat_show">
            <h4>{newtext}<br/> <h5>{time1}</h5> </h4>
         </div>
         <br/>
         <form>
         <input type="text" placeholder="Type a message" onChange={onEvent} value={text} />
         <button onClick={onSubmit} >HEllo</button>
         </form>
        </div>
    </>)
}
export default Chat;
