import React, { useState } from 'react';
import './index.css';
import ContactDetails from './ContactDetails';
const AddContact =(props)=>{
    const[Txt,setTxt] =useState({
        title:" ",
        contact: " "
    });
  const [newTxt,upTxt]=useState([""]);
    const onEvent=(main)=>{
      console.log(setTxt);
      const{name, value} = main.target;

      setTxt((preData) => {
          return {
              ...preData,
              [name] : value, 
           };
        // console.log(setTxt);
      });
    };   
    const addCon=()=>{
     upTxt((kal)=>{
      return [
          ...kal,Txt]
      
  })
       console.log(addCon);
     props.preventDefault();
   
  setTxt({
    title:" ",
    contact: " "
});
    }
    return(<>
        <div className = "addContact">
            <form>
                <label>Enter Name* </label>
                <input type="text" required 
                onChange={onEvent} 
                name="title"
                 value={Txt.title}
                 autoComplete="off"
               />
                <br/>
                <label>Enter Number*</label>
                 <input type="number" 
                  minLength="10"
                  required
                   onChange={onEvent}
                   name="contact"
                   value={Txt.contact} 
                />
                <br/>
                <button onClick={addCon} >Add Contact</button>
            </form>

        </div>
        <div>
 {       newTxt.map((sel,index)=>{
         return( <ContactDetails
             key={index}
             id={index}
             Ammo={sel.title}
             Memmo={sel.contact}
         />)}
)}
 
      </div> 
    </>)
    } 
    export default AddContact;
