import React, { useState } from 'react';
import './index.css';
const AddContact =(props)=>{
    const[txt,setTxt]=useState({
        title:" ",
        contact:" "
    });

    const  onEvent =(main)=>{
        setTxt(main.target.value);
        setTxt(main.target.name);
        
    }
    const addCon=(even)=>{
        
        even.preventDefault();
        props.fatch(txt);
    }
    return(<>
        <div className = "addContact">
            <form>
            <table className="tabelcontact">
              <tr>
               <td> <label>Enter Name* </label></td><td> <input type="text" required onChange={onEvent} value={txt.title} name="title" /></td>
                </tr>
                <br/>
                <tr>
                <td><label>Enter Number*</label> </td><td><input type="text" minLength="10" required onChange={onEvent} value={txt.contact} name="contact"/></td>
                </tr>
                <br/>
                </table>
                <button onClick={addCon}>Add Contact</button>
            </form>
        </div>

                

    </>)
}
export default AddContact;
