import React, { useState } from 'react';
import './index.css';
import ContactDetails from './ContactDetails';
const AddContact =()=>{
    const[Txt,setTxt] =useState({
        title:" ",
        contact: " "
    });
    const [newTxt,upTxt]=useState([" "]);
    const onEvent=(main)=>{
      console.log(setTxt);
      const{name, value} = main.target;

      setTxt((preData) => {
          return {
              ...preData,
              [name] : value, 
           };
        // console.log(setTxt);
      });
    };   
    const addCon=(cal)=>{
    upTxt((kal)=>{
        return [
            ...kal,Txt]
        
    })
       console.log(addCon);
     cal.preventDefault();
     newTxt.map((sel,index)=>{
         <ContactDetails
             key={index}
             id={index}
             Ammo={sel.title}
             Memmo={sel.contact}
         />
  setTxt({
    title:" ",
    contact: " "
});
     })
    }
    return(<>
        <div className = "addContact">
            <form>
                <label>Enter Name* </label>
                <input type="text" required 
                onChange={onEvent} 
                name="title"
                 value={Txt.title}
                 autoComplete="off"
               />
                <br/>
                <label>Enter Number*</label>
                 <input type="text" 
                 minLength="10"
                  required
                   onChange={onEvent}
                   name="contact"
                   value={Txt.contact} 
                />
                <br/>
                <button onClick={addCon} >Add Contact</button>
            </form>
        </div>
    </>)
    }
    export default AddContact;
