import React, { useState } from 'react';
import './index.css';
import ContactDetails from './ContactDetails';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';
const AddContact =(props)=>{
    const[Txt,setTxt] =useState({
        title:" ",
        contact: " "
    });
  const [newTxt,upTxt]=useState([""]);
  const [expnd,newExpnd]=useState(false);
    const onEvent=(main)=>{
      console.log(setTxt);
      const{name, value} = main.target;

      setTxt((preData) => {
          return {
              ...preData,
              [name] : value, 
           };
        // console.log(setTxt);
      });
    };   
    const addCon=(even)=>{
     upTxt((kal)=>{
      return [
          ...kal,Txt]
      
  })
       console.log(addCon);
     even.preventDefault();
   
  setTxt({
    title:" ",
    contact: " "
});
    }
    const Expand=()=>{
      newExpnd(true);
    }
   const normal=()=>{
     newExpnd(false);
   }
    return(<>
        <div className = "addContact"  >
            <form  >
            {expnd?
            <div>
            <button onClick={normal}>-</button>
                <label>Enter Name* </label>
                <input type="text" required 
                onChange={onEvent} 
                name="title"
                 value={Txt.title}
                 autoComplete="off"
               />
                <br/>
                <label>Enter Number*</label>
                 <input type="number" 
                  minLength="10"
                  required
                   onChange={onEvent}
                   name="contact"
                   value={Txt.contact} 
                />
                <br/>
                <Button variant="contained" color="primary"  >add contact</Button></div>:null}
            </form>
            <div className="buttonAdd">
            <Button variant="contained" color="outline" onClick={addCon} onClick={Expand}><AddIcon/></Button></div>
        </div>
        <div>
 {     newTxt.map((sel,index)=>{
        return( <ContactDetails
            key={index}
            id={index}
            Ammo={sel.title}
            Memmo={sel.contact}
        />)}
)}
 
      </div> 
    </>)
    } 
    export default AddContact;
