import React, { useState } from 'react';
import './index.css';
import ContactDetails from './ContactDetails';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';
import HighlightOffSharpIcon from '@material-ui/icons/HighlightOffSharp';
const ListContact =(props)=>{
  const[Txt,setTxt] =useState({
    title:" ",
    contact: " "
});
const [newTxt,upTxt]=useState([""]);
const [expnd,newExpnd]=useState(false);
const onEvent=(main)=>{
  console.log(setTxt);
  const{name, value} = main.target;

  setTxt((preData) => {
      return {
          ...preData,
          [name] : value, 
       };
    // console.log(setTxt);
  });
};   
const addCon=(even)=>{
 upTxt((kal)=>{
  return [
      ...kal,Txt]
  
})
   console.log(addCon);
 even.preventDefault();

setTxt({
title:" ",
contact: " "
});
}
const Expand=()=>{
  newExpnd(true);
}
const normal=()=>{
 newExpnd(false);
}

    return(<>
           <div className="contList">
           <div className="buttonAdd">
            <Button variant="contained" color="primary"  onClick={Expand}><AddIcon/></Button></div>
        
 <h6 style={{border:"0px solid red",textAlign:"center"}} >Contact numbers</h6>
                      
 {     newTxt.map((sel,index)=>{
        return( <ContactDetails
            key={index}
            id={index}
            Ammo={sel.title}
            Memmo={sel.contact}
        />)}
)}  

</div>
      
           <div className = "addContact"  >
            <form  >
            {expnd?
            <div>
            <Button onClick={normal} style={{backgroundColor:"red",marginLeft:"10px",textAlign:"right" }}><HighlightOffSharpIcon/></Button><br/>
                <label>Enter Name* </label>
                <input type="text" required 
                onChange={onEvent} 
                name="title"
                 value={Txt.title}
                 autoComplete="off"
               />
                <br/>
                <label>Enter Number*</label>
                 <input type="number" 
                  minLength="10"
                  required
                   onChange={onEvent}
                   name="contact"
                   value={Txt.contact} 
                />
                <br/>
                <Button variant="contained" color="primary" onClick={addCon}  >Add Contact</Button></div>:null}
            </form>
          
    </div>
             </>)
}
export default ListContact;
