import React, { useState } from 'react';
import './index.css';
const AddContact =()=>{
    const[Txt,setTxt] =useState({
        title:" ",
        contact: " "
    });
    const onEvent=(main)=>{
      setTxt(main.target.value);
      setTxt(main.target.name);
      console.log(setTxt);
    }   
    return(<>
        <div className = "addContact">
            <form>
            <table className="tabelcontact">
              <tr>
               <td> <label>Enter Name* </label></td><td> <input type="text" required onChange={onEvent}/></td>
                </tr>
                <br/>
                <tr>
                <td><label>Enter Number*</label> </td><td><input type="text" minLength="10" required onChange={onEvent} /></td>
                </tr>
                <br/>
                </table>
                <button onClick={addCon} >Add Contact</button>

            </form>
        </div>

                

    </>)
}
export default AddContact;
