import React from 'react';
import './index.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Background from './Image/Background.jpeg';
import { NavLink } from 'react-router-dom';
const Forget =()=>{
    return(<>
           <div style={{backgroundColor:"red"}} className="forget_password">
               <input type="email" placeholder ="Your Email*" required/>
               <span>or</span>
           </div>
    </>)
}

const Login =()=>{
    return(<>
     <img src = {Background}  alt="img"  width = "100%" height="575px" />  
     <div className="login_form">
          <form>
          <lable>Member Login</lable><br/>
              <input type="text" placeholder="User Id*" required/>
              <br/>
              <input type ="password" placeholder ="Password*" required/>
              <br/>
              <br/>
              <button  class="btn btn-outline-secondary">Login</button>
          </form>
              <NavLink exact to ='/forgetpassword' ><span style={{display:"inline-block",fontSize:"18px",marginLeft:"170px", color:"#E71E18",textDecoration:"underline",textDecorationColor:"red"}}>Forget Password*</span></NavLink>

    </div></>)
}

export default Login;
export {Forget};
